INSERT INTO `destoon_webpage` VALUES (1,'1',0,0,'锦江之星','','<div class=\"lemma-summary\">\r\n<div class=\"para\"><strong>酒店介绍：<br />\r\n</strong><br />\r\n锦江之星是国内知名的<a target=\"_blank\" href=\"http://baike.baidu.com/view/648196.htm\">快捷酒店</a>品牌，创立于1996年。</div>\r\n<div class=\"para\">创立至今，旗下各品牌酒店总数已超1000多家，分布在全国31个省、直辖市，200多个城市。客房总数超100000间。</div>\r\n<div class=\"para\">锦江之星提供便捷的酒店快速预订、会员特价预订、地图查询预订等特色服务。目前旗下品牌有锦江之星快捷酒店、金广快捷酒店、百时快捷酒店、<a target=\"_blank\" href=\"http://baike.baidu.com/subview/15186/5044255.htm\" data-lemmaid=\"867\">白玉兰</a>、锦江都城等。<br />\r\n&nbsp;</div>\r\n</div>\r\n<strong>民警优惠政策：</strong><br />\r\n<br />\r\n凡住店民警均送水壶一个。','','','','admin',1478313465,5,1,0,'about/jinjiang.html','','');
